# Banner
echo "" | base64 -d

# Installing Requirements
sudo apt update
sudo apt install zsh kitty rofi xclip batcat lsd feh picom ranger build-essential git xcb libxcb-util0-dev libxcb-ewmh-dev libxcb-randr0-dev libxcb-icccm4-dev libxcb-keysyms1-dev libxcb-xinerama0-dev libasound2-dev libxcb-xtest0-dev libxcb-shape0-dev sudo apt install cmake cmake-data pkg-config python3-sphinx libcairo2-dev libxcb1-dev libxcb-util0-dev libxcb-randr0-dev libxcb-composite0-dev python3-xcbgen xcb-proto libxcb-image0-dev libxcb-ewmh-dev libxcb-icccm4-dev libxcb-xkb-dev libxcb-xrm-dev libxcb-cursor-dev libasound2-dev libpulse-dev libjsoncpp-dev libmpdclient-dev libcurl4-openssl-dev libnl-genl-3-dev meson libxext-dev libxcb1-dev libxcb-damage0-dev libxcb-xfixes0-dev libxcb-shape0-dev libxcb-render-util0-dev libxcb-render0-dev libxcb-randr0-dev libxcb-composite0-dev libxcb-image0-dev libxcb-present-dev libxcb-xinerama0-dev libpixman-1-dev libdbus-1-dev libconfig-dev libgl1-mesa-dev libpcre2-dev libevdev-dev uthash-dev libev-dev libx11-xcb-dev libxcb-glx0-dev -y

# Copying .config files
cp -r .config/* /home/$USER/.config

# Installing HackNerdFonts
sudo cp fonts/* /usr/share/fonts
fc-cache -f -v

# Installing Powerlevel10k
cp -r powerlevel10k ~/powerlevel10k
cp .p10k ~/.p10k

# Copying zsh-config
cp .zshrc ~/.zshrc

# Configuring ranger
ranger --copy-config=all

# Making bspwm
cd bspwm
make
sudo make install
sudo apt update
sudo apt install bspwm -y

# Making sxhkd
cd ..
cd sxhkd
make
sudo apt update
cd ..

# Adding WallPapers to bspwm
cp images/wallpapers/* ~/Images/Wallpapers







